mapper.update:
UPDATE member
pwd = #{pwd}, name = #{name}, gender = #{gender}, email = #{email}, phone = #{phone},
WHERE userid = #{userid}

mapper.select:
SELECT *
FROM qna
WHERE userid = #{userId}
ORDER BY qseq DESC
    LIMIT #{paging.startNum}, #{paging.displayRow}

SELECT COUNT(*)
FROM qna
WHERE userid = #{userId}

SELECT COUNT(*)
FROM users
WHERE userid = #{userid} AND pwd = #{pwd}
